import React from 'react';
import dayjs from 'dayjs';
import './App.scss';

import { ReactComponent as LogoSvg } from "./assets/img/logo.svg";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      contacts: [],
    }
  }

  componentDidMount() {
    fetch('http://5e82ac6c78337f00160ae496.mockapi.io/api/v1/contacts', {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    })
      .then( response => response.json())
      .then(data => this.setState({ contacts: data }))
  }

  render() {
    const { contacts } = this.state;

    console.log(contacts);

    return (
      <React.Fragment>
        <header className="topbar">
          <div className="container">
            <a href="/" className="topbar__logo">
              <LogoSvg alt="Logo Instagram" />
            </a>
          </div>
        </header>

        <div className="container">
          <section className="filters">
            <div className="filters__search">
              <input type="text" className="filters__search__input" placeholder="Pesquisar" />

              <button className="filters__search__icon">
                <i className="fa fa-search"/>
              </button>
            </div>

            <button className="filters__item is-selected">
              Nome <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              País <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              Empresa <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              Departamento <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              Data de admissão <i className="fas fa-sort-down" />
            </button>
          </section>
        </div>

        <div className="container">
          <section className="contacts">
            <article className="contact">
            <span className="contact__avatar"></span>

            <span className="contact__data">Nome</span>

              <span className="contact__data">Telefone</span>

              <span className="contact__data">
                    {contact.country}
                  </span>

              <span className="contact__data">
                    {dayjs(contact.admissionDate).format('DD/MM/YYYY')}
                  </span>

              <span className="contact__data">
                    {contact.company}
                  </span>

              <span className="contact__data">
                    {contact.department}
                  </span>
            </article>
            {contacts.map((contact) => (
                <article
                  key={contact.id}
                  className="contact"
                >
                  <span className="contact__avatar">
                    <img src={contact.avatar} alt={contact.name} />
                  </span>

                  <span className="contact__data">
                    {contact.name}
                  </span>

                  <span className="contact__data">
                    {contact.phone}
                  </span>

                  <span className="contact__data">
                    {contact.country}
                  </span>

                  <span className="contact__data">
                    {dayjs(contact.admissionDate).format('DD/MM/YYYY')}
                  </span>

                  <span className="contact__data">
                    {contact.company}
                  </span>

                  <span className="contact__data">
                    {contact.department}
                  </span>
                </article>
              ))
            }
          </section>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
