import React from 'react';
import './App.scss';

import { ReactComponent as LogoSvg } from "./assets/img/logo.svg";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      contacts: [],
    }
  }

  componentDidMount() {
    fetch('http://5e82ac6c78337f00160ae496.mockapi.io/api/v1/contacts', {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    })
      .then( response => response.json())
      .then(data => this.setState({ contacts: data }))
  }

  render() {
    const { contacts } = this.state;

    console.log(contacts);

    return (
      <React.Fragment>
        <header className="topbar">
          <div className="container">
            <a href="/" className="topbar__logo">
              <LogoSvg alt="Logo Instagram" />
            </a>

            <div className="topbar__search">
              <input type="text" className="topbar__search__input" placeholder="Pesquisar" />

              <button className="topbar__search__icon">
                <i className="fa fa-search"/>
              </button>
            </div>
          </div>
        </header>

        <div className="container">
          <section className="filters">
            <h6 className="filters__title">Filtrar por:</h6>

            <button className="filters__item is-selected">
              Nome <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              País <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              Empresa <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              Departamento <i className="fas fa-sort-down" />
            </button>

            <button className="filters__item">
              Data de admissão <i className="fas fa-sort-down" />
            </button>
          </section>
        </div>

        <div className="container">
          <section className="list">
            {contacts.map((contact) => (
                <article
                  key={contact.id}
                  className="list-item"
                >
                  <span>{contact.name}</span>
                </article>
              ))
            }
          </section>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
